INSERT INTO public.safety_precautions (safety_id, problem_id, safety_description, warning_level, precaution_type, hazard_type, ppe_required, emergency_procedure, created_at, updated_at) VALUES
('e2587462-7e8f-45ff-9e93-8e262187fc2f', 'e363b9f7-029a-4996-bc99-44f189defb40', 'JANGAN BUKA TUTUP RADIATOR SAAT MESIN PANAS! Coolant bertekanan dapat menyembur dan menyebabkan luka bakar serius. Tunggu minimal 30 menit hingga mesin dingin.', 'Danger', 'Mechanic Safety', 'High Temperature', 'Sarung tangan tahan panas, kacamata safety, long sleeve shirt', 'Jika terkena coolant panas: segera siram dengan air dingin mengalir selama minimal 20 menit. Jika luka bakar parah, segera ke UGD.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('57bb293a-ab35-4b07-9687-454fc9a4db2a', 'e363b9f7-029a-4996-bc99-44f189defb40', 'Jangan jalankan kendaraan yang overheat - dapat menyebabkan kerusakan mesin permanen seperti warped head, seized engine.', 'Warning', 'Vehicle Safety', 'Mechanical', 'N/A', 'Jika overheat: segera menepi, matikan AC, nyalakan heater untuk membantu buang panas, matikan mesin. Jangan buka kap sampai cukup dingin.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('ed752554-8a03-4512-8086-c931dc51ea30', '7eb9d443-0e90-4ec4-a79e-57e0e1915974', 'Hati-hati saat bekerja dengan sistem pengapian. Tegangan tinggi dari ignition coil (20.000-40.000V) dapat menyebabkan kejutan listrik.', 'Warning', 'Mechanic Safety', 'Electrical', 'Sarung tangan isolasi, alas karet, kacamata safety', 'Jika terkena kejutan: jauhi sumber listrik, periksa kesadaran korban, hubungi bantuan medis jika diperlukan.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('ea094607-b8e2-468a-a8dd-c71f57ab42a7', '7eb9d443-0e90-4ec4-a79e-57e0e1915974', 'Buang busi bekas, oli, dan komponen dengan benar. Jangan buang ke tempat sampah biasa.', 'Caution', 'Environmental', 'Chemical', 'Sarung tangan nitril', 'Kumpulkan limbah B3 dan serahkan ke pengepul resmi.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('134dc321-1f53-4907-a5f1-fcfd14972289', 'a1f896bd-cad1-4a63-a655-90c5073caca7', 'Oli mesin panas dapat menyebabkan luka bakar. Pastikan mesin sudah dingin sebelum mengganti oli.', 'Warning', 'Mechanic Safety', 'High Temperature', 'Sarung tangan tahan panas, kacamata safety, apron', 'Jika terkena oli panas: siram dengan air dingin, oleskan salep luka bakar.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('c68800db-1925-4c86-bff7-077f3e2c066c', 'a1f896bd-cad1-4a63-a655-90c5073caca7', 'Oli bekas adalah limbah B3. Jangan buang ke tanah atau saluran air.', 'Caution', 'Environmental', 'Chemical', 'Sarung tangan nitril', 'Tampung oli bekas dalam wadah tertutup dan serahkan ke pengepul resmi.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('164bfe0d-93ce-4346-bc76-d32f5ecd66ec', 'e2f599f5-3b72-4de8-bf87-76ce854a8325', 'Bahan bakar sangat mudah terbakar! Jangan bekerja dekat api, rokok, atau sumber percikan api. Pastikan ventilasi cukup.', 'Danger', 'Mechanic Safety', 'Fire/Explosion', 'Sarung tangan nitril, kacamata safety, pakaian anti-statis, fire extinguisher ready', 'Jika terjadi kebakaran: gunakan fire extinguisher Class B, jauhi area, hubungi pemadam.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('4f8b7012-ee0e-491b-9ad4-3c3b009fa861', 'e2f599f5-3b72-4de8-bf87-76ce854a8325', 'Discharge static electricity sebelum membuka fuel system. Sentuh body kendaraan terlebih dahulu.', 'Warning', 'Vehicle Safety', 'Electrical', 'Gelang anti-statis', 'Static discharge dapat menyebabkan percikan dan ledakan.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00'),
('00000000-0000-0000-0000-000000000001', 'b87b26e6-0212-411a-8ca5-11fc0e20cbff', 'Kebocoran bahan bakar adalah bahaya SANGAT SERIUS. Jangan jalankan kendaraan sama sekali jika ada kebocoran yang terdeteksi!', 'Danger', 'Mechanic Safety', 'Fire/Explosion', 'Full PPE, fire extinguisher, well-ventilated area only', 'Jika tercium bau bensin kuat: jangan hidupkan apapun yang bisa memicu percikan, ventilasi area, evakuasi.', '2026-01-02 07:31:40.042796+00', '2026-01-02 07:31:40.042796+00');
